export default function balance(
  state = { value: 0 },
  action: { type: string; payload?: number }
) {
  switch (action.type) {
    case "UPDATE_BALANCE":
      return { value: action.payload };
    case "CREDIT":
      return { value: state.value - (action.payload ? action.payload : 0) };
    default:
      return state;
  }
}

