interface State {
  value: number;
}

const defaultState: State = {
  value: 0,
};

export default function balance(
  state = defaultState,
  action: { type: string; payload?: number }
) {
  switch (action.type) {
    case "UPDATE_BALANCE":
      return { value: action.payload as number };
    default:
      return state;
  }
}

